import React, {Component} from 'react';
import '../scss/binario.scss'




class Binario extends Component {

  state=
      {
          'NumberBinaire': 0,
          'NumberDecimale':0,
          'Resultato':0,
          'message':'',
          'B':0,
          'N':0
      }


    Vedi=(e)=>{
        e.preventDefault();
        var modulo=0;

        var list=[];
        if(this.state.NumberBinaire == 0 || this.state.NumberDecimale==0){
            this.setState(this.state,()=>{
                this.state.Resultato=0;
            })
            this.Risultato(this.state.Resultato)
        }else {
            if(this.state.NumberBinaire) {
                while (this.state.NumberBinaire >= 2) {
                    modulo=this.state.NumberBinaire % 2
                    list.push(modulo)
                }
                this.setState(this.state,()=>{
                    this.state.Resultato=list;
                })
                console.log(list)
                this.Risultato(this.state.Resultato)
            }
            if(this.state.NumberDecimale!==0) {

            }
        }

    }
    span(Resultato){
      return("<h6>"+"Risultato : "+"<span>"+Resultato+"</span>"+"<h6>");
    }

    Risultato(Resultato) {
      var elements =document.getElementsByClassName('Resultato')
    for (var i =0 ;i<elements.length;i++){
        elements[i].innerHTML=this.span(this.state.Resultato)
    }
    }

    GetValueBin=(e)=>{

      let nouvo=e.target.value
     this.setState(this.state,()=> {
           if (isNaN(parseInt(nouvo))===false){
               this.state.NumberBinaire=nouvo;
               this.state.message='';
               this.state.B=0;
           }else{
               this.state.message='Veuillez Inserer Un Numbre';
               this.state.B=1;
           }
         }
     )

    }


    GetValueDic=(e)=>{
        let nouv=e.target.value
        this.setState(this.state,()=> {
                if (isNaN(parseInt(nouv))===false){
                    this.state.message='';
                    this.state.NumberDecimale=nouv;
                    this.state.D=0;
                }else{
                    this.state.message='Veuillez Inserer Un Numbre'
                    this.state.D=1;
                }
            }
        )
    };

      Item=()=>{

          if(this.state.B===1 && this.state.message){
              return <h6>{this.state.message}</h6>;

          }
          if(this.state.D===1 && this.state.message){
              return <h6>{this.state.message}</h6>;

          }
      };
    render() {
        return (
            <div className='binario'>
               <h3> conversion <br />base [ 10 --> 2  &&  [ 2 --> 10 ]</h3>
               <div className='forms'>
                  <div className='form'>
                      <div className='space'>
                          <h5>convert To binario</h5>
                          <div className='errore'>
                              {this.Item()}
                          </div>
                          <form onSubmit={this.Vedi} className='form_10_2 '>
                              <input type="text" placeholder="Your Number" className="txtb" name='base10' onChange={this.GetValueBin} />
                              <button className='convert' > ->  </button>
                          </form>
                          <div className='Resultato'>

                          </div>
                      </div>
                      <div className='space'>
                          <h5>convert To decimal</h5>
                          <div className='errore'>
                              {this.Item()}
                          </div>
                          <form onSubmit={this.Vedi} className='form_2_10' >
                              <input type="text" placeholder="Your Number" className="txtb" name='base10' onChange={this.GetValueDic}/>
                              <button className='convert' > -> </button>
                          </form>
                          <div className='Resultato'>

                          </div>
                      </div>
                  </div>
               </div>
            </div>
        );
    }
}

export default Binario;
