import React, {Component} from 'react';
import '../scss/binario.scss'




class Binario extends Component {

  state=
      {
          'NumberBinaire': 0,
          'NumberDecimale':[],
          'Resultato':0,
          'message':'',
          'B':0,
          'N':0
      }


    Vedi=(e)=>{
        e.preventDefault();
        let modulo = 0;
        let list = [];
        let List=[];
        if(this.state.NumberBinaire == 0 || this.state.NumberDecimale==0){
            this.Risultato(this.state.Resultato)
        }
        if (parseInt(this.state.NumberBinaire) != 0) {
                let i = 0;
               let rest=parseInt(this.state.NumberBinaire)
                while ( rest>0) {
                    modulo = rest % 2;
                    rest =  parseInt(rest / 2);
                    list[i] = modulo;
                    i++;
                    console.log(list)
                }

                for (let s = 0; s < list.length; s++) {
                    List[s] = list[list.length - s - 1]
                }
                this.setState(this.state, () => {
                    this.state.Resultato = List;
                    console.log(" risult   "+this.state.Resultato)
                })
                console.log("List  "+List)
                this.Risultato(List)
            }
            if (this.state.NumberDecimale !== 0) {

            }


    }
    span(Resultato){
      return("<h6>"+"Risultato : "+"<span>"+Resultato+"</span>"+"</h6>");
    }

    Risultato(Resultato) {
      var elements =document.getElementsByClassName('Resultato')
    for (var i =0 ;i<elements.length;i++){
        elements[i].innerHTML=this.span(Resultato)

    }
        console.log( "final  "+ Resultato)
    }

    GetValueBin=(e)=>{

      let nouvo =e.target.value
     this.setState(this.state,()=> {
         console.log(isNaN(parseInt(nouvo)))
           if (isNaN(parseInt(nouvo))===false){
               this.state.NumberBinaire=parseInt(nouvo);
               this.state.message='';
               this.state.B=0;

           }else{
               this.state.message='Veuillez Inserer Un Numbre';
               this.state.B=1;
           }

         }
     )
        let element =document.getElementsByClassName('Resultato');

       element[0].innerHTML=null;
    }


    GetValueDic=(e)=>{
        let nouv=e.target.value
        this.setState(this.state,()=> {
                if (isNaN(parseInt(nouv))===false){
                    this.state.message='';
                    this.state.NumberDecimale.push(parseInt(nouv));
                    this.state.D=0;
                }else{
                    this.state.message='Veuillez Inserer Un Numbre'
                    this.state.D=1;
                }
            }

        )
        console.log(this.state.NumberDecimale)
        var element =document.getElementsByClassName('Resultato');
        console.log(element)
        element.innerHTML=null;

    };

      Item=()=>{

          if(this.state.B===1 && this.state.message){
              return <h6>{this.state.message}</h6>;

          }
          if(this.state.D===1 && this.state.message){
              return <h6>{this.state.message}</h6>;

          }
      };
    render() {
        return (
            <div className='binario'>
               <h3> conversion <br />base [ 10 --> 2  &&  [ 2 --> 10 ]</h3>
               <div className='forms'>
                  <div className='form'>

                      <div className='space'>
                          <h5>convert To binario</h5>
                          <div className='errore'>
                              {this.Item()}
                          </div>
                          <form onSubmit={this.Vedi} className='form_10_2 '>
                              <input type="text" placeholder="Your Number" className="txtb" name='base10' onChange={this.GetValueBin} />
                              <button className='convert' > ->  </button>
                          </form>

                      </div>
                      <div className='space'>
                          <h5>convert To decimal</h5>
                          <div className='errore'>
                              {this.Item()}
                          </div>
                          <form onSubmit={this.Vedi} className='form_2_10' >
                              <input type="text" placeholder="Your Number" className="txtb" name='base10' onChange={this.GetValueDic}/>
                              <button className='convert' > -> </button>
                          </form>
                      </div>
                      <div className='Resultato space'>
                      </div>
                  </div>

               </div>

            </div>
        );
    }
}

export default Binario;
